## Helper functions


# return if n is identical to its reverse
function ispalindrome(n)
    string(n) == reverse(string(n))
end


# compute an upper bound for the nth prime
function nth_prime_ulimit(n)
    if n < 6
        throw(DomainError())
    end
    return iceil(n * log(n * log(n)))
end


# sieve of eratosthenes
# find all the primes below n
function esieve(n)
    candidates = vcat([2], [3:2:n])
    i = 1
    while i < length(candidates)
        i += 1
        if candidates[i] == 0
            continue
        end
        v = candidates[i]
        candidates[i+v:v:end] = 0
    end
    return filter(n -> n != 0, candidates)
end


# find the maximum product in `dta` of `chunksize` adjacent numbers in
# the 1st dimension
function max_down_product(dta, chunksize)
    cur_max = 0
    for row in 1:(size(dta)[1] - (chunksize-1))
        products = prod(dta[row:row+(chunksize-1), :], 1)
        p_max = maximum(products)
        cur_max = p_max > cur_max ? p_max : cur_max
    end
    return cur_max
end


# find the maximum product in `dta` of `chunksize` adjacent numbers
# taken diagonally
function max_diag_product(dta, chunksize)
    cur_max = 0
    for row in 1:(size(dta)[1] - (chunksize-1))
        dta_section = dta[row:row+(chunksize-1), :]
        for col in 0:(size(dta)[2] - chunksize)
            product = prod(diag(dta_section, col))
            cur_max = product > cur_max ? product : cur_max
        end
    end
    return cur_max
end


# convert a string to an array of digits
function todigits(s)
    return [int(string(c)) for c in string(s)]
end


# compute all the prime factors of n
function prime_factors(n)
    primes = Int64[]
    i = 2
    while n != 1
        if n % i == 0
            if isempty(primes) || (last(primes) != i)
                append!(primes, [i])
            end
            n = div(n, i)
        else
            i += 1
        end
    end
    return primes
end


# endlessly produce the elements in arr
function cyclic(arr)
    while true
        for x in arr
            produce(x)
        end
    end
end


# cyclically xor the values in key with data
function decrypt(key, dta)
    keyval = @task cyclic(key)
    return [x $ consume(keyval) for x in dta]
end


const printable_ascii_range = int(' '):int('~')


# return if all the numbers in `dta` are in the printable ascii range
function is_printable_ascii(dta)
    return all(x -> (x in printable_ascii_range), dta)
end


# compute a dictionary of counts of each element in `dta`
function counts(dta)
    counts_ = Dict()
    for d in dta
        counts_[d] = get(counts_, d, 0) + 1
    end
    return counts_
end


# take an array of ints (ascii-values) and process it into a string
function array_to_string(dta)
    return join([string(char(x)) for x in dta], "")
end


# are the most frequent characters ' ' and 'e', and does it contain
# " the "
function is_english(dta)
    counts_ = counts(dta)
    sorted = sort(collect(keys(counts_)), by=x->counts_[x])
    if (sorted[end] == int(' ')) &&
       (sorted[end-1] == int('e')) &&
       (ismatch(r" the ", array_to_string(dta)))
        return true
    else
        return false
    end
end




## Problems


# Compute all the multiples of 3 and 5 below `ulimit`
function problem1(ulimit=999)
    valid = i -> (i % 3 == 0) | (i % 5 == 0)
    sum(filter(valid, 1:ulimit))
end


# Sum all fibonnacci numbers below `ulimit`
function problem2(i1=0, i2=1, ulimit=4e6)
    sum = 0
    while i2 < ulimit
        if i2 % 2 == 0
            sum += i2
        end
        i1, i2 = i2, i1 + i2
    end
    return sum
end


# compute the largest prime factor of `n`
function problem3(n=600851475143)
    return last(prime_factors(n))
end


# compute the largest palindrome made from the product of two 3 digit
# numbers
function problem4()
    range = [x for x in 100:999]
    candidates = reverse(sort(kron(range, range)))
    for c in candidates
        if ispalindrome(c)
            return c
        end
    end
end

# compute the smallest multiple of 1:20
function problem5()
    divisors = [19 18 17 16 15 14 13 12 11]
    i = 0
    done = false
    while ~done
        i += 20
        done = true
        for d in divisors
            if i % d != 0
                done = false
                break
            end
        end
    end
    return i
end

# compute square-of-sum - sum-of-squares of 1:ulimit
function problem6(ulimit=100)
    square_of_sum = sum([x for x in 1:ulimit])^2
    sum_of_squares = sum([x^2 for x in 1:ulimit])
    return square_of_sum - sum_of_squares
end


# compute the 10001st prime number
function problem7(n=10001)
    ulimit = nth_prime_ulimit(n)
    return esieve(ulimit)[n]
end


