# return the pythagorean triple for which a + b + c == 1000
function problem9()
    for c in 1:1000
        for b in 1:c
            for a in 1:b
                if (a + b + c == 1000) && (a^2 + b^2 == c^2)
                    return a*b*c
                end
            end
        end
    end
end


# find the sum of all primes below n
function problem10(n=2000000)
    return sum(esieve(n))
end


# find the greatest product of four adjacent numbers
function problem11()
    chunksize = 4
    dta = problem_11_dta

    p1, p2, p3, p4 = (max_down_product(dta, chunksize),
                      max_down_product(transpose(dta), chunksize),
                      max_diag_product(dta, chunksize),
                      max_diag_product(fliplr(dta), chunksize))

    return max(p1, p2, p3, p4)
end


