# compute the sum of the digits of 2^1000
function problem16()
    return sum(todigits(BigInt(2)^1000))
end


# compute the sum of the digits of 100!
function problem20()
    return sum(todigits(factorial(BigInt(100))))
end


# determine if i can be written as the sum of a prime and twice a square
function prime_square(i, primes, squares)
    for p in primes
        if p > i
            break
        end
        for s in squares
            summation = p + s
            if summation > i
                break
            elseif summation == i
                return true
            end
        end
    end
    return false
end


# compute the smallest odd composite number that cannot be written as
# the sum of a prime and twice a square
function problem46(ulimit=int(2e6))
    primes = esieve(ulimit)
    prime_set = IntSet()
    union!(prime_set, primes)
    squares = [2*(x^2) for x in 1:int(ceil(sqrt(ulimit/2)))]
    i = 1
    while true
        i += 2
        if !(i in prime_set) && !check_prime_square(i, primes, squares)
            return i
        end
    end
end


# compute the first consecutive `n` numbers to have `n` distinct prime
# factors
function problem47(n=4)
    i = 2
    streak_len = n
    streak = Int64[]

    while true
        pfs = prime_factors(i)
        if length(pfs) == n
            append!(streak, [i])
        else
            streak = Int64[]
        end
        if length(streak) == n
            return first(streak)
        end
        i += 1
    end

end


# find the prime below `ulimit` that is the sum of the longest number of
# consecutive primes
function problem50(ulimit=int(1e6))
    max_len = 0
    prime_sum  = 0

    primes = esieve(ulimit)
    prime_set = IntSet()
    union!(prime_set, primes)

    for start in 1:length(primes)
        summation = sum(primes[start:])
        for stop in length(primes):-1:start
            if (summation < ulimit)
                cur_len = length(start:stop)
                if (cur_len > max_len) && (summation in prime_set)
                    prime_sum = summation
                    max_len = cur_len
                end
            end
            summation -= primes[stop]
        end
    end
    return prime_sum, max_len
end


# decrypt the xor-ed ascii text
# this function gets close but still requires some manual inspection
function problem59(dta=problem_59_dta)
    key_range = int('a'):int('z')

    key = [0, 0, 0]
    plaintext = []
    done = false
    for i in key_range, j in key_range, k in key_range
        key = [i, j, k]
        plaintext = decrypt(key, dta)
        if is_printable_ascii(plaintext) && is_english(plaintext)
            println(array_to_string(key))
            println(array_to_string(plaintext))
